import { Component, OnInit, OnDestroy, ViewEncapsulation } from '@angular/core';
import { CommonModule, NgFor, NgIf } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { Router, RouterLink } from '@angular/router';
import { DomSanitizer, SafeHtml } from '@angular/platform-browser';
import { UserProfile } from '../../shared/services/auth.service';
import { PostService, PostData, CommentData } from '../../shared/services/post.service';

export interface FeedPost extends PostData {
  likes: number;
  liked: boolean;
  hovered: boolean;
  commentsCount: number;
  renderedHtml: SafeHtml;
  authorInitials: string;
  authorColor: string;
}

export interface Creator {
  name: string;
  initials: string;
  handle: string;
  color: string;
  followed: boolean;
}

export interface Activity {
  icon: string;
  text: string;
  time: string;
}

const FLORAL = 'data:image/png;base64,';


@Component({
  selector: 'app-home',
  standalone: true,
  imports: [CommonModule, FormsModule, NgFor, NgIf, RouterLink],
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css'],
  encapsulation: ViewEncapsulation.None 
})


export class HomeComponent implements OnInit, OnDestroy {

  profile: UserProfile | null = null;
  stats = { posts: 0, followers: '1.2k', likes: 0 };
  activeNav = 'feed';
  notifCount = 3;
  activeFilter: 'all' | 'minimal' | 'editorial' | 'citation' | 'portrait' = 'all';
  activeSort = 'recent';
  sortOptions = [
    { label: 'Récents',     value: 'recent' },
    { label: 'Populaires',  value: 'popular' },
    { label: 'Abonnements', value: 'followed' },
  ];
  searchQuery = '';
  private searchTimer: any;
  toastMessage = '';
  toastVisible = false;
  private toastTimer: any;
  posts: FeedPost[] = [];

  commentingPost: FeedPost | null = null;
  comments: CommentData[] = [];
  loadingComments = false;
  newComment = '';
  sendingComment = false;

  viewingPost: FeedPost | null = null;

  myComments: CommentData[] = [];
  loadingMyComments = false;
  showMyCommentsModal = false;

  editingCommentId: number | null = null;
  editingCommentContent: string = '';

  trendingTags = ['#motivation','#art','#lifestyle','#citation','#bienêtre','#mode','#voyage','#nature'];

  suggestedCreators: Creator[] = [
    { name: 'Sophie M.',    initials: 'SM', handle: '@sophiemrt',   color: '#7a3520', followed: false },
    { name: 'Léa B.',       initials: 'LB', handle: '@leab_writes', color: '#0d1f3c', followed: false },
    { name: 'Marie Claire', initials: 'MC', handle: '@marieclaire', color: '#7a2e4a', followed: false },
  ];

  recentActivity: Activity[] = [
    { icon: '♡', text: '<strong>Sophie M.</strong> a aimé votre post',      time: 'Il y a 5 min' },
    { icon: '⟳', text: '<strong>Léa B.</strong> a partagé votre citation',  time: 'Il y a 23 min' },
    { icon: '✦', text: '<strong>Axel R.</strong> a commencé à vous suivre', time: 'Il y a 1h' },
  ];

  private themeMap: Record<string, Record<string, string>> = {
    bordeaux:   { accent: '#5c1a1a', 'accent-mid': '#7a2e2e', 'accent-warm': '#8b3a3a', 'accent-tan': '#c8a882', 'accent-tan2': '#d4b896' },
    noir:       { accent: '#1a1a1a', 'accent-mid': '#2e2e2e', 'accent-warm': '#3d3d3d', 'accent-tan': '#8a8a8a', 'accent-tan2': '#aaaaaa' },
    navy:       { accent: '#0d1f3c', 'accent-mid': '#1a3660', 'accent-warm': '#1e4080', 'accent-tan': '#7a9cbf', 'accent-tan2': '#a0bdd6' },
    forest:     { accent: '#1a3320', 'accent-mid': '#2e5538', 'accent-warm': '#3a6645', 'accent-tan': '#7aaa82', 'accent-tan2': '#a0c4a8' },
    terracotta: { accent: '#7a3520', 'accent-mid': '#9e4a2c', 'accent-warm': '#b25630', 'accent-tan': '#d4956a', 'accent-tan2': '#e0b090' },
    blush:      { accent: '#7a2e4a', 'accent-mid': '#9e3d60', 'accent-warm': '#b54a72', 'accent-tan': '#d4859a', 'accent-tan2': '#e0aab8' },
    olive:      { accent: '#3a3a18', 'accent-mid': '#555524', 'accent-warm': '#666630', 'accent-tan': '#a0a060', 'accent-tan2': '#baba80' },
    caramel:    { accent: '#6b3a10', 'accent-mid': '#8e4e1a', 'accent-warm': '#a85e20', 'accent-tan': '#d4a060', 'accent-tan2': '#e0bc88' },
  };

  private styleFilterMap: Record<string, string[]> = {
    minimal: ['style7', 'style8', 'style11'], 
    editorial: ['style2', 'style9', 'style10', 'style12'],
    citation: ['style14', 'style15', 'style16'],
    portrait: ['style1', 'style3', 'style4', 'style5', 'style6', 'style13', 'style17', 'style18', 'style19', 'style20']
  };


  constructor(
    private router: Router,
    private sanitizer: DomSanitizer,
    private postService: PostService
  ) {}

  ngOnInit(): void { this.loadPosts(); }

  ngOnDestroy(): void {
    clearTimeout(this.toastTimer);
    clearTimeout(this.searchTimer);
  }

  loadPosts(): void {
    this.postService.getPosts().subscribe({
      next: (data) => {
        this.posts = data.map(p => this.toFeedPost(p));
        this.stats.posts = this.posts.length;
        this.stats.likes = this.posts.reduce((acc, p) => acc + p.likes, 0);
      },
      error: (err) => console.error('Erreur chargement posts', err)
    });
  }

  private toFeedPost(p: PostData): FeedPost {
    const colors = ['#5c1a1a','#7a3520','#0d1f3c','#1a3320','#7a2e4a','#3a3a18','#6b3a10'];
    const color = colors[p.author_id % colors.length];
    const initials = (p.author_username || 'U').split(' ').map(w => w[0]).join('').toUpperCase().slice(0, 2);
    return {
      ...p,
      likes: 0, liked: false, hovered: false, commentsCount: p.comments_count ?? 0,
      renderedHtml: this.renderPost(p),
      authorInitials: initials,
      authorColor: color,
    };
  }

  private renderPost(p: PostData): SafeHtml {
    const colors = this.themeMap[p.theme] ?? this.themeMap['bordeaux'];
    return this.sanitizer.bypassSecurityTrustHtml(this.buildPostHtml(p, colors));
  }

  private cssVars(colors: Record<string, string>): string {
    return Object.entries(colors).map(([k, v]) => `--${k}:${v}`).join(';');
  }

  private imgBg(p: PostData, cls = '', ex = ''): string {
  const imgUrl = p.image ? `http://localhost:8002${p.image}` : '';
  const s = imgUrl
    ? `background-image:url(${imgUrl});background-size:cover;background-position:center;`
    : 'background:#c8bba4;';
  return `<div class="p-photo ${cls}" style="${s}${ex}"></div>`;
}

  private txtBg(p: PostData): string {
    if (p.bg === 'none')     return 'background:transparent;';
    if (p.bg === 'gradient') return 'background:linear-gradient(135deg,#e8dcc8 0%,#d4b896 50%,#c8a882 100%);';
    if (p.bg === 'floral')   return `background-color:#f0ece3;background-image:url(${FLORAL});background-size:cover;background-position:center;`;
    return 'background:#e8dcc8;';
  }

  private buildPostHtml(p: PostData, colors: Record<string, string>): string {
    const vars = this.cssVars(colors);
    const wrap = (inner: string) =>
      `<div class="feed-post-inner post-${p.style}" style="${vars}">${inner}</div>`;

    const v = { w: p.accroche, t: p.title, q: p.citation, b: p.content, a: p.auteur_signature, h: p.handle };

    switch (p.style) {
      case 'style1': return wrap(
        `<div class="s1-wrap">${this.imgBg(p,'s1-bg-photo')}<div class="s1-overlay"></div>` +
        `<div class="s1-top-label">${v.w}</div>` +
        `<div class="s1-card"><div class="s1-avatar">${this.imgBg(p,'s1-avatar-img')}</div>` +
        `<h2 class="s1-title">${v.t}</h2><p class="s1-body">${v.b}</p><span class="s1-handle">${v.h}</span></div></div>`);

      case 'style2': return wrap(
        `<div class="s2-wrap"><div class="s2-top">${this.imgBg(p,'s2-photo')}` +
        `<h1 class="s2-word">${v.w}</h1></div>` +
        `<div class="s2-bottom" style="${this.txtBg(p)}"><p class="s2-body">${v.b}</p></div></div>`);

      case 'style3': return wrap(
        `<div class="s3-wrap"><div class="s3-left"><span class="s3-vertical">${v.t}</span></div>` +
        `<div class="s3-center"><div class="s3-arch">${this.imgBg(p,'s3-arch-photo')}</div>` +
        `<div class="s3-circle"></div></div>` +
        `<div class="s3-right"><span class="s3-vertical-r">${v.w}</span></div>` +
        `<span class="s3-handle">${v.h}</span></div>`);

      case 'style4': return wrap(
        `<div class="s4-wrap">${this.imgBg(p,'s4-photo')}` +
        `<div class="s4-content"><div class="s4-arch-card">` +
        `<h2 class="s4-title">${v.t}</h2><p class="s4-body">${v.b}</p><span class="s4-handle">${v.h}</span></div></div></div>`);

      case 'style5': return wrap(
        `<div class="s5-wrap"><div class="s5-top"><span class="s5-handle">${v.h}</span></div>` +
        `${this.imgBg(p,'s5-photo')}<div class="s5-curve"></div>` +
        `<div class="s5-content"><h2 class="s5-title">${v.t}</h2><p class="s5-body">${v.b}</p></div></div>`);

      case 'style6': return wrap(
        `<div class="s6-wrap"><div class="s6-left" style="${this.txtBg(p)}">` +
        `<h1 class="s6-big-title">${v.t}</h1><p class="s6-body">${v.b}</p></div>` +
        `<div class="s6-right">${this.imgBg(p,'s6-photo')}</div></div>`);

      case 'style7': return wrap(
        `<div class="s7-wrap" style="${this.txtBg(p)}">` +
        `<div class="s7-corner-tl"></div><div class="s7-corner-br"></div>` +
        `<div class="s7-top-deco"></div><p class="s7-eyebrow">${v.w}</p>` +
        `<h1 class="s7-title">${v.t}</h1><div class="s7-rule"></div>` +
        `<p class="s7-body">${v.b}</p><span class="s7-handle">${v.h}</span></div>`);

      case 'style8': return wrap(
        `<div class="s8-wrap"><span class="s8-word s8-word-top">${v.w}</span>` +
        `<div class="s8-outer-ring"><div class="s8-circle">${this.imgBg(p,'','width:100%;height:100%;')}</div></div>` +
        `<span class="s8-word s8-word-bot">${v.h}</span></div>`);

      case 'style9': return wrap(
        `<div class="s9-wrap"><div class="s9-diag-bg"></div>${this.imgBg(p,'s9-photo')}` +
        `<div class="s9-content"><p class="s9-eyebrow">${v.w}</p>` +
        `<h2 class="s9-title">${v.t}</h2><p class="s9-body">${v.b}</p>` +
        `<span class="s9-handle">${v.h}</span></div></div>`);

      case 'style10': return wrap(
        `<div class="s10-wrap">${this.imgBg(p,'s10-top-photo')}` +
        `<p class="s10-photo-label">${v.w}</p>` +
        `<div class="s10-card" style="${this.txtBg(p)}">` +
        `<div class="s10-top-accent"></div>` +
        `<h2 class="s10-title">${v.t}</h2><p class="s10-body">${v.b}</p>` +
        `<span class="s10-handle">${v.h}</span></div></div>`);

      case 'style11': return wrap(
        `<div class="s11-wrap"><div class="s11-frame"></div><div class="s11-inner">` +
        `<div class="s11-photo">${this.imgBg(p,'','width:100%;height:100%;')}</div>` +
        `<div class="s11-ornament">&#10022; &#10022; &#10022;</div>` +
        `<h2 class="s11-title">${v.t}</h2><div class="s11-rule"></div>` +
        `<p class="s11-body">${v.b}</p><span class="s11-handle">${v.h}</span></div></div>`);

      case 'style12': return wrap(
        `<div class="s12-wrap">${this.imgBg(p,'s12-photo')}` +
        `<p class="s12-photo-word">${v.w}</p>` +
        `<div class="s12-content" style="${this.txtBg(p)}">` +
        `<div class="s12-accent-bar"></div>` +
        `<h2 class="s12-title">${v.t}</h2><p class="s12-body">${v.b}</p>` +
        `<span class="s12-handle">${v.h}</span></div></div>`);

      case 'style13': return wrap(
        `<div class="s13-wrap">${this.imgBg(p,'s13-bg')}` +
        `<div class="s13-overlay"></div>` +
        `<div class="s13-oval" style="${this.txtBg(p)}">` +
        `<p class="s13-sub">${v.w}</p><h2 class="s13-script">${v.t}</h2>` +
        `<p class="s13-body">${v.b}</p><span class="s13-author">${v.a}</span></div></div>`);

      case 'style14': return wrap(
        `<div class="s14-wrap" style="${this.txtBg(p)}">` +
        `<div class="s14-bgword"></div>` +
        `<div class="s14-content"><div class="s14-quote-mark">&#8220;</div>` +
        `<p class="s14-quote-text">${v.q}</p></div></div>`);

      case 'style15': return wrap(
        `<div class="s15-wrap"><div class="s15-top" style="${this.txtBg(p)}">` +
        `<span class="s15-eyebrow">&#8220;</span><p class="s15-big-quote">${v.q}</p></div>` +
        `<div class="s15-bottom"><div class="s15-circle">${this.imgBg(p,'','width:100%;height:100%;')}</div>` +
        `<div class="s15-divider"></div></div></div>`);

      case 'style16': return wrap(
        `<div class="s16-wrap">${this.imgBg(p,'s16-photo')}` +
        `<div class="s16-overlay"></div>` +
        `<div class="s16-content"><p class="s16-quote">&#8220;${v.q}&#8221;</p>` +
        `<span class="s16-author">${v.a}</span></div></div>`);

      case 'style17': return wrap(
        `<div class="s17-wrap" style="${this.txtBg(p)}">` +
        `<div class="s17-left"><div class="s17-arch">${this.imgBg(p,'','width:100%;height:100%;')}</div></div>` +
        `<div class="s17-right"><div class="s17-deco"><span class="s17-handle">${v.h}</span></div>` +
        `<h2 class="s17-script">Welcome!</h2><p class="s17-body">${v.b}</p>` +
        `<span class="s17-author">${v.a}</span></div></div>`);

      case 'style18': return wrap(
        `<div class="s18-wrap" style="${this.txtBg(p)}"><div class="s18-bg-word"></div>` +
        `<div class="s18-circle">${this.imgBg(p,'','width:100%;height:100%;')}</div>` +
        `<div class="s18-content"><p class="s18-script">Hello,</p>` +
        `<h2 class="s18-title">${v.t}</h2><p class="s18-body">${v.b}</p>` +
        `<div class="s18-footer"><span>${v.h}</span></div></div>` +
        `<div class="s18-bg-word bot"></div></div>`);

      case 'style19': return wrap(
        `<div class="s19-wrap">${this.imgBg(p,'s19-bg-photo','width:100%;height:100%;')}` +
        `<div class="s19-overlay"></div>` +
        `<div class="s19-card"><p class="s19-script">About</p>` +
        `<h2 class="s19-title">${v.t}</h2><p class="s19-body">${v.b}</p>` +
        `<span class="s19-handle">${v.h}</span></div></div>`);

      case 'style20': return wrap(
        `<div class="s20-wrap"><div class="s20-left" style="${this.txtBg(p)}">` +
        `<p class="s20-script">About</p><h2 class="s20-big">${v.t}</h2>` +
        `<p class="s20-body">${v.b}</p>` +
        `<div class="s20-footer"><span>${v.h}</span><span>${v.a}</span></div></div>` +
        `<div class="s20-right">${this.imgBg(p,'s20-photo')}<div class="s20-plant"></div></div></div>`);

      default: return wrap(`<p>${p.title}</p>`);
    }
  }

  get userInitials(): string {
    if (!this.profile) return '?';
    const fn = this.profile.first_name?.[0] ?? '';
    const ln = this.profile.last_name?.[0] ?? '';
    return (fn + ln).toUpperCase() || this.profile.username?.[0]?.toUpperCase() || 'U';
  }


  get filteredPosts(): FeedPost[] {
    let result = [...this.posts];

    if (this.activeFilter !== 'all') {
      const allowedStyles = this.styleFilterMap[this.activeFilter] || [];
      result = result.filter(p => allowedStyles.includes(p.style));
    }

    if (this.searchQuery.trim().length > 1) {
      const q = this.searchQuery.toLowerCase();
      result = result.filter(p =>
        p.title?.toLowerCase().includes(q) ||
        p.content?.toLowerCase().includes(q) ||
        p.citation?.toLowerCase().includes(q) ||
        p.author_username?.toLowerCase().includes(q) ||
        p.handle?.toLowerCase().includes(q)
      );
    }
    if (this.activeSort === 'popular') {
      result = [...result].sort((a, b) => b.likes - a.likes);
    }

    return result;
  }

  setNav(nav: string): void {
    this.activeNav = nav;
    if (nav === 'myposts') this.loadMyPosts();
    else if (nav === 'feed') this.loadPosts();
    else if (nav === 'commentaire') this.openMyComments();
    this.showToast(nav === 'feed' ? "Fil d'actualité" : nav);
  }

  loadMyPosts(): void {
    this.postService.getMyPosts().subscribe({
      next: (data) => { this.posts = data.map(p => this.toFeedPost(p)); },
      error: (err) => console.error(err)
    });
  }

  goToProfile(): void  { this.router.navigate(['/profile']); }
  goToCreate(): void   { this.router.navigate(['/posts']); }
  goToSettings(): void { this.showToast('Paramètres'); }
  logout(): void       { this.router.navigate(['/login']); }

  filterStyle(style: typeof this.activeFilter): void {
    this.activeFilter = style;
    this.showToast(style === 'all' ? 'Tous les styles' : 'Filtre : ' + style);
  }

  setSort(value: string): void {
    this.activeSort = value;
    this.showToast(this.sortOptions.find(s => s.value === value)?.label ?? value);
  }

  handleSearch(): void {
    clearTimeout(this.searchTimer);
    if (this.searchQuery.trim().length > 2) {
      this.searchTimer = setTimeout(() => this.showToast('Recherche : ' + this.searchQuery.trim()), 600);
    }
  }

  searchTag(tag: string): void { this.searchQuery = tag; this.handleSearch(); this.showToast('Tendance : ' + tag); }
  toggleLike(post: FeedPost): void { post.liked = !post.liked; post.likes += post.liked ? 1 : -1; }
  commentPost(post: FeedPost): void {
    this.commentingPost = post;
    this.comments = [];
    this.newComment = '';
    this.loadingComments = true;
    this.postService.getComments(post.id).subscribe({
      next: (data) => { this.comments = data; this.loadingComments = false; },
      error: () => { this.loadingComments = false; }
    });
  }
  viewPost(post: FeedPost): void {
    this.viewingPost = post;
  }
  closeViewModal(): void {
    this.viewingPost = null;
  }


  editPost(post: FeedPost): void    { this.router.navigate(['/posts/edit', post.id]); }

  toggleFollow(creator: Creator): void {
    creator.followed = !creator.followed;
    this.showToast(creator.followed ? 'Abonné à ' + creator.name : 'Abonnement annulé');
  }

  showToast(message: string): void {
    clearTimeout(this.toastTimer);
    this.toastMessage = message;
    this.toastVisible = true;
    this.toastTimer = setTimeout(() => { this.toastVisible = false; }, 1800);
  }

  closeCommentModal(): void {
    this.commentingPost = null;
    this.comments = [];
    this.newComment = '';
  }

  submitComment(): void {
    if (!this.newComment.trim() || !this.commentingPost || this.sendingComment) return;
    this.sendingComment = true;
    this.postService.createComment(this.commentingPost.id, this.newComment.trim()).subscribe({
      next: (c) => {
        this.comments = [c, ...this.comments];
        this.commentingPost!.commentsCount++;
        // Mettre à jour le post dans le feed
        const idx = this.posts.findIndex(p => p.id === this.commentingPost!.id);
        if (idx !== -1) this.posts[idx].commentsCount = this.commentingPost!.commentsCount;
        this.newComment = '';
        this.sendingComment = false;
      },
      error: () => { this.sendingComment = false; }
    });
  }

  formatCommentDate(dateStr: string): string {
    const d = new Date(dateStr);
    const now = new Date();
    const diff = Math.floor((now.getTime() - d.getTime()) / 1000);
    if (diff < 60) return 'À l\'instant';
    if (diff < 3600) return `Il y a ${Math.floor(diff / 60)} min`;
    if (diff < 86400) return `Il y a ${Math.floor(diff / 3600)}h`;
    return d.toLocaleDateString('fr-FR', { day: 'numeric', month: 'short' });
  }

  commentInitials(username: string): string {
    return (username || 'U').slice(0, 2).toUpperCase();
  }


  openMyComments(): void {
    this.showMyCommentsModal = true;
    this.loadingMyComments = true;
    this.myComments = [];
    this.postService.getMyComments().subscribe({
      next: (data) => { this.myComments = data; this.loadingMyComments = false; },
      error: () => { this.loadingMyComments = false; }
    });
  }

  closeMyCommentsModal(): void {
    this.showMyCommentsModal = false;
    this.myComments = [];
  }

  startEditComment(c: CommentData): void {
    this.editingCommentId = c.id;
    this.editingCommentContent = c.content;
  }

  cancelEditComment(): void {
    this.editingCommentId = null;
    this.editingCommentContent = '';
  }

  saveEditComment(c: CommentData): void {
    if (!this.editingCommentContent.trim()) return;
    this.postService.updateComment(c.id, this.editingCommentContent.trim()).subscribe({
      next: (updated) => {
        const idx = this.myComments.findIndex(x => x.id === c.id);
        if (idx !== -1) this.myComments[idx].content = updated.content;
        this.cancelEditComment();
        this.showToast('Commentaire modifié');
      },
      error: () => this.showToast('Erreur lors de la modification')
    });
  }

  deleteMyComment(c: CommentData): void {
    this.postService.deleteComment(c.id).subscribe({
      next: () => {
        this.myComments = this.myComments.filter(x => x.id !== c.id);
        this.showToast('Commentaire supprimé');
      },
      error: () => this.showToast('Erreur lors de la suppression')
    });
  }
}
